mapper.select:
select
  user_id, user_account, create_date, last_active_date
from clock_user


        and user_id = #{userId}


        and user_account = #{userAccount}


        and create_date = #{createDate}


        and last_active_date = #{lastActiveDate}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from clock_user


        and user_id = #{userId}


        and user_account = #{userAccount}


        and create_date = #{createDate}


        and last_active_date = #{lastActiveDate}
